import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const PatrimoineChart = ({ initialData }) => {
  const [data, setData] = useState(initialData);

  useEffect(() => {
    // Ajoute une nouvelle date toutes les 5 secondes pour simuler un ajout de données
    const interval = setInterval(() => {
      const lastDate = new Date(data[data.length - 1].date);
      const newDate = new Date(lastDate);
      newDate.setDate(lastDate.getDate() + 1);

      const newValue = data[data.length - 1].valeur + Math.floor(Math.random() * 100); // Simulation de nouvelle valeur
      setData([...data, { date: newDate.toISOString().split('T')[0], valeur: newValue }]);
    }, 5000);

    return () => clearInterval(interval);
  }, [data]);

  const chartData = {
    labels: data.map(item => item.date),
    datasets: [
      {
        label: 'Valeur du Patrimoine',
        data: data.map(item => item.valeur),
        fill: false,
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(75,192,192,1)',
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Évolution du Patrimoine',
      },
    },
  };

  return (
    <div className="chart-container">
      <Line data={chartData} options={options} />
    </div>
  );
};

export default PatrimoineChart;
