import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '..pages/PossessionListPage.css'; // Assurez-vous que le fichier CSS est importé

function PossessionListPage() {
  const [possessions, setPossessions] = useState([]);

  useEffect(() => {
    fetchPossessions();
  }, []);

  const fetchPossessions = () => {
    axios.get('http://localhost:5000/possession')
      .then(response => {
        setPossessions(response.data);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des possessions:', error);
      });
  };

  const handleClosePossession = (libelle) => {
    axios.put(`http://localhost:5000/possession/${libelle}/close`)
      .then(response => {
        console.log('Possession clôturée avec succès:', response.data);
        fetchPossessions(); // Recharger la liste des possessions après la clôture
      })
      .catch(error => {
        console.error('Erreur lors de la clôture de la possession:', error);
      });
  };

  const handleEditPossession = (libelle) => {
    window.location.href = `/possession/${libelle}/update`;
  };

  return (
    <div className="possession-list-container">
      <header className="possession-header">
        <h1>Liste des Possessions</h1>
        <div className="button-group">
          <button className="create-button" onClick={() => window.location.href = '/possession/create'}>
            Créer une nouvelle Possession
          </button>
          <button className="home-button" onClick={() => window.location.href = '/'}>
            Retour à l'accueil
          </button>
        </div>
      </header>
      <table className="possession-table">
        <thead>
          <tr>
            <th>Libellé</th>
            <th>Valeur</th>
            <th>Date Début</th>
            <th>Date Fin</th>
            <th>Taux</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {possessions.map((possession, index) => (
            <tr key={index}>
              <td>{possession.libelle}</td>
              <td>{possession.valeur}</td>
              <td>{possession.date_debut}</td>
              <td>{possession.date_fin || 'N/A'}</td>
              <td>{possession.taux}</td>
              <td>
                <button className="close-button" onClick={() => handleClosePossession(possession.libelle)}>Clôturer</button>
                <button className="edit-button" onClick={() => handleEditPossession(possession.libelle)}>Modifier</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PossessionListPage;
